import { faTrash, faPenSquare } from '@fortawesome/free-solid-svg-icons';
import { Validators } from '@angular/forms';
export class HomeComponent {
    constructor(restauranteSerice, form) {
        this.restauranteSerice = restauranteSerice;
        this.form = form;
        this.listaRestaurantes = [];
        this.nomeRestaurante = [];
        this.faTrash = faTrash;
        this.faPenSquare = faPenSquare;
    }
    ngOnInit() {
        this.getRestaurantes();
        this.formRestaurante = this.form.group({
            nomeRestaurante: ['', Validators.required]
        });
    }
    async getRestaurantes() {
        await this.restauranteSerice.listarRestaurantes().then(res => {
            this.listaRestaurantes = res;
        });
    }
    async adicionarRestaurantes() {
        const inBody = {
            nome: this.formRestaurante.get('nomeRestaurante').value
        };
        console.log(inBody);
        await this.restauranteSerice.addRestaurantes(inBody).then(() => {
            alert('Deu certo a chamda');
            this.getRestaurantes();
        }).catch();
    }
    async deletarRestaurantes(idRestaurante) {
        const inBody = {
            id: idRestaurante
        };
        await this.restauranteSerice.removerRestaurantes(inBody).then(() => {
            this.getRestaurantes();
        }).catch(err => {
            console.log(err);
        });
    }
    async editarRestaurante(id) {
        const inBody = {
            id: id
        };
        await this.restauranteSerice.getRestauranteID(inBody).then(data => {
            this.nomeRestaurante = data;
            this.formRestaurante.get('nomeRestaurante').setValue(this.nomeRestaurante.nome);
        });
    }
}
