import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { faTrash } from '@fortawesome/free-solid-svg-icons';
import { faPenSquare } from '@fortawesome/free-solid-svg-icons';
let HomeComponent = class HomeComponent {
    constructor(restauranteSerice) {
        this.restauranteSerice = restauranteSerice;
        this.listaRestaurantes = [];
        this.model = {};
        this.faTrash = faTrash;
        this.faPenSquare = faPenSquare;
    }
    ngOnInit() {
        this.getRestaurantes();
    }
    async getRestaurantes() {
        await this.restauranteSerice.listarRestaurantes().then(res => {
            this.listaRestaurantes = res;
        });
    }
    async adicionarRestaurantes() {
        const inBody = {
            nome: 'this.model.nome'
        };
        await this.restauranteSerice.addRestaurantes(inBody).then().catch();
    }
    async deletarRestaurantes(param) {
        const inBody = {
            id: param
        };
        await this.restauranteSerice.removerRestaurantes(inBody);
    }
};
HomeComponent = __decorate([
    Component({
        selector: 'app-home',
        templateUrl: './home.component.html',
    })
], HomeComponent);
export { HomeComponent };
